import React from "react";
import Button from "@material-ui/core/Button";
import Product from "../utils/Product";
import TextField from "@material-ui/core/TextField";

class SearchItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      title: "",
      price: "",
      descripition: "",
      image: "",
    };
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const prod = {
      id: this.id,
    };
    console.log("prod" + prod);
    const loadProduct = () => {
      Product.getProducts()
        .then((res) => {
          const data = res.data;
          console.log("Line 45 " + data);
          data.map((data) =>
            setProd({
              product: {
                id: data.name,
                title: data.title,
                price: data.price,
                descripition: data.descripition,
                image: data.image,
              },
            })
          );
        })
        .catch(() => {
          console.log("could not find data");
        });
    };
    loadProduct();
  };

  handleChange = (e) => {
    console.log(e.target.id);
    this.setState({
      [e.target.id]: e.target.value,
    });
  };
  render() {
    return (
      <div className="">
        <TextField
          id="id"
          label="Search Item"
          style={{ margin: 0 }}
          placeholder="item #"
          fullWidth
          value={this.handleChange}
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          variant="outlined"
        />
        <Button onClick={this.handleChange} variant="contained" color="primary">
          Primary
        </Button>
      </div>
    );
  }
}

export default SearchItem;
