import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";

// Pages(Routes)
import Login from "./pages/Login";
import MainMenu from "./pages/MainMenu";
import NoMatch from "./pages/NoMatch";

// Components
import Nav from "./components/Nav";
import Employee from "./utils/Employee";

class App extends Component {
  state = {
    employee: [
      {
        name: "",
        employeeNum: "",
        password: "",
      },
    ],
  };

  componentDidMount = () => {
    this.loadEmpl();
  };

  loadEmpl = () => {
    Employee.getEmployees()
      .then((res) => {
        const data = res.data;
        this.setState({ employee: {name: data.name, employeeNum: data.employeeNum, password: data.password}})
      })
      .catch(() => {
        console.log("could not find data");
      });
  };

  render() {
    return (
      <Router>
        <div className="col-lg-12">
          <Nav />

          <Route
            exact
            path="/"
            render={(props) => (
              <React.Fragment>
                <Login />
              </React.Fragment>
            )}
          />
         <Route
            exact
            path="/mainmenu"
            render={(props) => (
              <React.Fragment>
                <MainMenu />
              </React.Fragment>
            )}
          />
          <Route path=" " component={NoMatch} />
        </div>
      </Router>
    );
  }
}

export default App;
