import React, { useState } from "react";
// import PropTypes from 'prop-types';
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";
import { signInUser } from "../authActions";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 200,
    },
  },
}));

class ValidationTextFields extends React.Component {
 classes = useStyles();

 constructor(props){
   super(props);
   this.state = {
     name: "",
    employeeNum: "",
    password: "",
    gradeLv: "",
   }
 }

  // const [value, setValue] = useState({
  //   name: "",
  //   employeeNum: "",
  //   password: "",
  //   gradeLv: "",
  // });

handleChange = (e) => {
    // console.log(e.target.name);
    setValue({
      [e.target.name]: e.target.value,
    });
    console.log("handleChange")
  };


 handleSubmit = (e) => {
    e.preventDefault();
    const user = {
      employeeNum: value.employeeNum,
      password: value.password,
    };
    console.log("Handle Submit" + user)
    signInUser(user).then((res) => {
      // console.log(res);
    });
  };
render(){

  return (
    <form
      className={classes.root}
      // onSubmit={handleSubmit}
      noValidate
      autoComplete="off"
    >
      <div>
        <TextField
          id="emplNum"
          onChange={handleChange}
          type="input"
          name="employeeNum"
          label="Employee #"
          value={value.emloyeeNum}
          variant="outlined"
        />
        <TextField
          id="password"
          onChange={handleChange}
          name="password"
          type="password"
          label="Password"
          value={value.password}
          variant="outlined"
        />
      </div>
      <Button variant="contained" id="button" onClick={handleSubmit}>Default</Button>
    </form>
  );
}
};

// ValidationTextFields.propTypes = {
//   signInUser: PropTypes.func.isRequired,
// };

export default ValidationTextFields;
